#map {
  height: 500px;
}

/* DTU Custom Popup Styles */
.custom-popup .leaflet-popup-content-wrapper {
  background: #fff;
  border-radius: 8px;
  box-shadow: 0 3px 14px rgba(0, 0, 0, 0.2);
}

.custom-popup .leaflet-popup-content {
  margin: 0;
  padding: 0;
}

.popup-content {
  min-width: 200px;
}

.popup-title {
  background: #003C71;
  color: white;
  margin: 0;
  padding: 10px 15px;
  font-size: 16px;
  border-radius: 8px 8px 0 0;
}

.popup-body {
  padding: 15px;
  color: #333;
  font-size: 14px;
  line-height: 1.4;
}

.popup-footer {
  padding: 8px 15px;
  background: #f5f5f5;
  color: #666;
  font-size: 12px;
  border-radius: 0 0 8px 8px;
  text-align: right;
}

.custom-popup .leaflet-popup-tip {
  background: #003C71;
}

